import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './SideMenu.css';

const SideMenu = ({ closeMenu, handleSignIn, handleSignOut }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const handleLinkClick = () => {
    toggleMenu(); // Close the menu when a link is clicked
    if (closeMenu) {
      closeMenu(); // Additional callback if provided
    }
  };

  const handleSignInClick = () => {
    handleSignIn(); // Handle sign-in action
    toggleMenu(); // Close the menu after sign-in
  };

  const handleSignOutClick = () => {
    handleSignOut(); // Handle sign-out action
    toggleMenu(); // Close the menu after sign-out
  };

  return (
    <div className={`side-menu ${isOpen ? 'open' : ''}`}>
      <button className="menu-toggle" onClick={toggleMenu}>
        {isOpen ? '≡' : ' ≡'}
      </button>
      {isOpen && (
        <nav>
          <ul>
            <li>
              <Link to="/" onClick={handleLinkClick}>Home</Link>
            </li>
            <li>
              <Link to="/create-task" onClick={handleLinkClick}>Create Task</Link>
            </li>
            <li>
              {handleSignIn && <button onClick={handleSignInClick}>Sign in</button>}
              {handleSignOut && <button onClick={handleSignOutClick}>Sign out</button>}
            </li>
          </ul>
        </nav>
      )}
    </div>
  );
};

export default SideMenu;
