import React, { useState, useEffect } from 'react';
import { auth } from './services/firebase';
import { signInWithPopup, GoogleAuthProvider, onAuthStateChanged, signOut } from 'firebase/auth';
import './App.css'; // Import your global CSS styles
import SideMenu from './components/SideMenu';
import CreateTask from './components/CreateTask';
import Home from './components/Home';
import History from './components/History';
import Preferences from './components/Preferences'; // Ensure correct path and export

import { Routes, Route, useNavigate } from 'react-router-dom'; // Ensure correct import statement

function App() {
  const [user, setUser] = useState(null);
  const [layout, setLayout] = useState('columns'); // Default layout is columns
  const [sideMenuColor, setSideMenuColor] = useState('#333'); // Default side menu color
  const [showPreferences, setShowPreferences] = useState(false); // State to toggle Preferences
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, user => {
      setUser(user);
    });
    return () => unsubscribe();
  }, []);

  const handleSignIn = async () => {
    const provider = new GoogleAuthProvider();
    try {
      await signInWithPopup(auth, provider);
    } catch (error) {
      console.error('Google sign-in error:', error);
    }
  };

  const handleSignOut = async () => {
    try {
      await signOut(auth);
      // Optionally, navigate to another page after sign out
    } catch (error) {
      console.error('Sign-out error:', error);
    }
  };

  const handleSavePreferences = (newLayout, newSideMenuColor) => {
    setLayout(newLayout);
    setSideMenuColor(newSideMenuColor);
    setShowPreferences(false); // Close Preferences after saving
  };

  const togglePreferences = () => {
    setShowPreferences(!showPreferences);
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <div className="welcome">
            {user && window.location.pathname === '/' && (
              <p>Welcome, {user.displayName}!</p>
            )}
          </div>
          <div className="auth-buttons">
            {user ? (
              <button className="sign-out-btn" onClick={handleSignOut}>Sign out</button>
            ) : (
              <button className="sign-in-btn" onClick={handleSignIn}>Sign in with Google</button>
            )}
          </div>
        </div>
      </header>
      <SideMenu user={user} sideMenuColor={sideMenuColor} />
      {/* Use nested routing for content */}
      <Routes>
        <Route path="/" element={<Home user={user} layout={layout} />} />
        <Route path="/create-task" element={<CreateTask />} />
        <Route path="/history" element={<History />} />
        {/* Route for Preferences component */}
        <Route path="/preferences" element={<Preferences onSavePreferences={handleSavePreferences} />} />
      </Routes>
      {/* Add a button or link to open Preferences */}
      {!showPreferences && (
        <button className="open-preferences-btn" onClick={togglePreferences}>Open Preferences</button>
      )}
    </div>
  );
}

export default App;
