import React, { useState, useEffect } from 'react';
import { db } from '../services/firebase'; // Assuming you have Firestore initialized here
import './Home.css'; // Adjust path as per your project structure

const Home = ({ user }) => {
  const [tasks, setTasks] = useState({
    'Not Started': [],
    'In Progress': [],
    'Needs Review': [],
    'Done': [],
  });

  useEffect(() => {
    const unsubscribe = db.collection('tasks').onSnapshot(snapshot => {
      const updatedTasks = {
        'Not Started': [],
        'In Progress': [],
        'Needs Review': [],
        'Done': [],
      };

      snapshot.forEach(doc => {
        const task = { id: doc.id, ...doc.data() };
        updatedTasks[task.progress].push(task);
      });

      setTasks(updatedTasks);
    });

    return () => unsubscribe();
  }, []);

  return (
    <div className="home">
      <div className="column">
        <h2>Not Started</h2>
        {tasks['Not Started'].map(task => (
          <div key={task.id} className="task-card">
            <h3>{task.taskName}</h3>
            <p>{task.description}</p>
            <p>Due: {task.dueDate}</p>
            <p>Priority: {task.priority}</p>
          </div>
        ))}
      </div>

      <div className="column">
        <h2>In Progress</h2>
        {tasks['In Progress'].map(task => (
          <div key={task.id} className="task-card">
            <h3>{task.taskName}</h3>
            <p>{task.description}</p>
            <p>Due: {task.dueDate}</p>
            <p>Priority: {task.priority}</p>
          </div>
        ))}
      </div>

      <div className="column">
        <h2>Needs Review</h2>
        {tasks['Needs Review'].map(task => (
          <div key={task.id} className="task-card">
            <h3>{task.taskName}</h3>
            <p>{task.description}</p>
            <p>Due: {task.dueDate}</p>
            <p>Priority: {task.priority}</p>
          </div>
        ))}
      </div>

      <div className="column">
        <h2>Done</h2>
        {tasks['Done'].map(task => (
          <div key={task.id} className="task-card">
            <h3>{task.taskName}</h3>
            <p>{task.description}</p>
            <p>Due: {task.dueDate}</p>
            <p>Priority: {task.priority}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
