import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import { auth } from './services/firebase';
import { signInWithPopup, GoogleAuthProvider, onAuthStateChanged, signOut } from 'firebase/auth';
import './App.css'; // Import your global CSS styles
import SideMenu from './components/SideMenu';
import CreateTask from './components/CreateTask';

function App() {
  const [user, setUser] = useState(null);
  const [isSideMenuOpen, setIsSideMenuOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, user => {
      setUser(user);
    });
    return () => unsubscribe();
  }, []);

  const handleSignIn = async () => {
    const provider = new GoogleAuthProvider();
    try {
      await signInWithPopup(auth, provider);
    } catch (error) {
      console.error('Google sign-in error:', error);
    }
  };

  const handleSignOut = async () => {
    try {
      await signOut(auth);
      navigate('/'); // This should work correctly now
    } catch (error) {
      console.error('Sign-out error:', error);
    }
  };

  const toggleSideMenu = () => {
    setIsSideMenuOpen(!isSideMenuOpen);
  };

  const closeSideMenu = () => {
    setIsSideMenuOpen(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <div className="welcome">
            {user && (
              <p>Welcome, {user.displayName}!</p>
            )}
          </div>
          <div className="auth-buttons">
            {user ? (
              <button className="sign-out-btn" onClick={handleSignOut}>Sign out</button>
            ) : (
              <button className="sign-in-btn" onClick={handleSignIn}>Sign in with Google</button>
            )}
          </div>
        </div>
      </header>
      <div className="content">
        <SideMenu isOpen={isSideMenuOpen} closeMenu={closeSideMenu} />
        <div className="task-lists">
          <div className="task-column">
            <h2>Not Started</h2>
            {/* Display tasks with progress "Not Started" */}
          </div>
          <div className="task-column">
            <h2>In Progress</h2>
            {/* Display tasks with progress "In Progress" */}
          </div>
          <div className="task-column">
            <h2>Needs Review</h2>
            {/* Display tasks with progress "Needs Review" */}
          </div>
          <div className="task-column">
            <h2>Done</h2>
            {/* Display tasks with progress "Done" */}
          </div>
        </div>
      </div>
      <Routes>
        <Route path="/create-task" element={<CreateTask />} />
        {/* Define more routes here */}
      </Routes>
    </div>
  );
}

export default App;
