// App.js
import './App.css';
import React, { useState, useEffect } from 'react';
import { auth } from './services/firebase'; // Adjust path as per your project structure
import { signInWithPopup, GoogleAuthProvider, onAuthStateChanged, signOut } from 'firebase/auth'; // Import specific Firebase auth methods
import SideMenu from './components/SideMenu'; // Import the SideMenu component

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, user => {
      setUser(user);
    });
    return () => unsubscribe();
  }, []);

  const handleSignIn = async () => {
    const provider = new GoogleAuthProvider();
    try {
      await signInWithPopup(auth, provider);
    } catch (error) {
      console.error('Google sign-in error:', error);
    }
  };

  const handleSignOut = async () => {
    try {
      await signOut(auth);
    } catch (error) {
      console.error('Sign-out error:', error);
    }
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          {/* Your header content */}
        </header>
      </div>
    </Router>
  );
}

export default App;
