import React, { useEffect, useState } from 'react';
import { db } from '../services/firebase';
import './Home.css'; // Ensure this path matches your project structure

const Home = ({ user }) => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const unsubscribe = db.collection('tasks')
      .orderBy('createdAt')
      .onSnapshot((snapshot) => {
        const fetchedTasks = snapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data(),
        }));
        setTasks(fetchedTasks);
      });

    return () => unsubscribe();
  }, []);

  const renderTasks = (status) => (
    tasks
      .filter(task => task.progress === status)
      .map(task => (
        <div key={task.id} className="task">
          <h3>{task.taskName}</h3>
          <p>{task.description}</p>
          <p>Due: {task.dueDate}</p>
          <p>Priority: {task.priority}</p>
        </div>
      ))
  );

  return (
    <div className="home-container">
      <h2>Welcome, {user ? user.displayName : 'Guest'}!</h2>
      <div className="tasks-board">
        <div className="tasks-column">
          <h3>Not Started</h3>
          {renderTasks('Not Started')}
        </div>
        <div className="tasks-column">
          <h3>In Progress</h3>
          {renderTasks('In Progress')}
        </div>
        <div className="tasks-column">
          <h3>Needs Review</h3>
          {renderTasks('Needs Review')}
        </div>
        <div className="tasks-column">
          <h3>Done</h3>
          {renderTasks('Done')}
        </div>
      </div>
    </div>
  );
};

export default Home;
