import React, { useEffect, useState } from 'react';
import { db } from '../services/firebase';
import { collection, query, orderBy, onSnapshot, deleteDoc, doc, setDoc, getDoc } from 'firebase/firestore';
import './Home.css'; // Ensure this path matches your project structure
import { useNavigate } from 'react-router-dom';

const Home = ({ user }) => {
  const [tasks, setTasks] = useState([]); // Assuming you manage tasks here
  const [deletedTasks, setDeletedTasks] = useState([]); // Define deletedTasks state
  const navigate = useNavigate();

  useEffect(() => {
    const q = query(
      collection(db, 'tasks'), // Query 'tasks' collection for active tasks
      orderBy('createdAt', 'desc')
    );

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const fetchedTasks = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setTasks(fetchedTasks); // Update tasks state
    }, (error) => {
      console.error('Error fetching tasks:', error);
    });

    return () => unsubscribe();
  }, []);

  const handleDelete = async (taskId) => {
    try {
      // Soft delete task from 'tasks' collection
      const taskRef = doc(db, 'tasks', taskId);
      await setDoc(taskRef, { status: 'deleted' }, { merge: true });
      console.log('Task soft deleted successfully with ID:', taskId);

      // Optionally, move task to 'deletedTasks' collection
      const taskSnapshot = await getDoc(taskRef);
      if (taskSnapshot.exists()) {
        const taskData = taskSnapshot.data();
        const deletedTaskRef = doc(db, 'deletedTasks', taskId);
        await setDoc(deletedTaskRef, {
          ...taskData,
          deletedAt: new Date(),
        });
        console.log('Task moved to deletedTasks collection with ID:', taskId);
      } else {
        console.error('Task not found in tasks collection:', taskId);
      }

      // Navigate or update state as needed
      navigate('/history');
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const renderTasks = (status) => (
    tasks
      .filter(task => task.status !== 'deleted' && task.progress === status)
      .map(task => (
        <div key={task.id} className="task-card">
          {/* Render task details */}
          <button className="close-btn" onClick={() => handleDelete(task.id)}>
            <span aria-hidden="true">&times;</span>
          </button>
        </div>
      ))
  );

  return (
    <div className="home-container">
      <h2>Welcome, {user ? user.displayName : 'Guest'}!</h2>
      <div className="tasks-board">
        <div className="column">
          <h3>Not Started</h3>
          {renderTasks('Not Started')}
        </div>
        <div className="column">
          <h3>In Progress</h3>
          {renderTasks('In Progress')}
        </div>
        <div className="column">
          <h3>Needs Review</h3>
          {renderTasks('Needs Review')}
        </div>
        <div className="column">
          <h3>Done</h3>
          {renderTasks('Done')}
        </div>
      </div>
    </div>
  );
};

export default Home;
