import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { auth } from '../services/firebase'; // Adjust path as per your project structure
import { signOut } from 'firebase/auth';
import './SideMenu.css';

const SideMenu = ({ isOpen, closeMenu }) => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setUser(user);
    });
    return () => unsubscribe();
  }, []);

  const handleSignOut = async () => {
    try {
      await signOut(auth);
      if (closeMenu) {
        closeMenu();
      }
    } catch (error) {
      console.error('Sign-out error:', error);
    }
  };

  const toggleMenu = () => {
    if (closeMenu) {
      closeMenu();
    }
  };

  return (
    <div className={`side-menu ${isOpen ? 'open' : ''}`}>
      <button className="menu-toggle" onClick={toggleMenu}>
        {isOpen ? '≡' : '≡'}
      </button>
      {isOpen && (
        <nav>
          <ul>
            <li>
              <Link to="/" onClick={toggleMenu}>
                Home
              </Link>
            </li>
            <li>
              <Link to="/create-task" onClick={toggleMenu}>
                Create Task
              </Link>
            </li>
            {user && (
              <li className="user-info">
                <img src={user.photoURL} alt="User Profile" className="user-avatar" />
                <span className="user-name">{user.displayName}</span>
              </li>
            )}
            <li>
              <button className="sign-out-btn" onClick={handleSignOut}>
                Sign out
              </button>
            </li>
          </ul>
        </nav>
      )}
    </div>
  );
};

export default SideMenu;
