import React, { useEffect, useState } from 'react';
import { db } from '../services/firebase';
import { collection, query, orderBy, onSnapshot, deleteDoc, doc } from 'firebase/firestore';
import './Home.css'; // Ensure this path matches your project structure

const Home = ({ user }) => {
  const [tasks, setTasks] = useState([]);
  const [showConfirmation, setShowConfirmation] = useState(true);

  useEffect(() => {
    const q = query(collection(db, 'tasks'), orderBy('createdAt'));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const fetchedTasks = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setTasks(fetchedTasks);
    });

    return () => unsubscribe();
  }, []);

  const deleteTask = async (taskId) => {
    try {
      await deleteDoc(doc(db, 'tasks', taskId));
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const confirmDelete = (taskId) => {
    if (!showConfirmation) {
      deleteTask(taskId);
      return;
    }

    const confirmed = window.confirm('Are you sure you want to remove this task?');
    if (confirmed) {
      deleteTask(taskId);
    }
  };

  const renderTasks = (status) => (
    tasks
     .filter(task => task.progress === status)
     .map(task => (
        <div key={task.id} className="task-card">
          <button className="close-btn" onClick={() => confirmDelete(task.id)}>Ã—</button>
          <h3>{task.taskName}</h3>
          <p>{task.description}</p>
          <p>Due: {task.dueDate}</p>
          <p>Priority: {task.priority}</p>
        </div>
      ))
  );

  const toggleConfirmation = () => {
    setShowConfirmation(prev => !prev);
  };

  return (
    <div className="home-container">
      <h2>Welcome, {user ? user.displayName : 'Guest'}!</h2>
      <div className="tasks-board">
        <div className="column">
          <h3>Not Started</h3>
          {renderTasks('Not Started')}
        </div>
        <div className="column">
          <h3>In Progress</h3>
          {renderTasks('In Progress')}
        </div>
        <div className="column">
          <h3>Needs Review</h3>
          {renderTasks('Needs Review')}
        </div>
        <div className="column">
          <h3>Done</h3>
          {renderTasks('Done')}
        </div>
      </div>
      <div className="confirmation-toggle">
        <label>
          <input type="checkbox" checked={!showConfirmation} onChange={toggleConfirmation} />
          Don't ask again
        </label>
      </div>
    </div>
  );
};

export default Home;
