import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './components/Home';
import History from './components/History';
import Preferences from './components/Preferences'; // Assuming you have a Preferences component

const App = () => {
  const [tasks, setTasks] = useState([]);

  const handleRestoreTask = (restoredTask) => {
    setTasks((prevTasks) => [...prevTasks, restoredTask]);
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home tasks={tasks} setTasks={setTasks} />} />
        <Route path="/history" element={<History onRestoreTask={handleRestoreTask} />} />
      </Routes>
    </Router>
  );
};

export default App;
