import './App.css';
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { auth } from './services/firebase';
import { signInWithPopup, GoogleAuthProvider, onAuthStateChanged, signOut } from 'firebase/auth';
import SideMenu from './components/SideMenu';
import CreateTask from './components/CreateTask'; // Import CreateTask component

function App() {
  const [user, setUser] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, user => {
      setUser(user);
    });
    return () => unsubscribe();
  }, []);

  const handleSignIn = async () => {
    const provider = new GoogleAuthProvider();
    try {
      await signInWithPopup(auth, provider);
    } catch (error) {
      console.error('Google sign-in error:', error);
    }
  };

  const handleSignOut = async () => {
    try {
      await signOut(auth);
    } catch (error) {
      console.error('Sign-out error:', error);
    }
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>Google Sign-In Demo</h1>
          {user ? (
            <>
              {window.location.pathname === '/' && <p>Welcome, {user.displayName}!</p>}
              <button onClick={handleSignOut}>Sign out</button>
              <SideMenu closeMenu={closeMenu} />
            </>
          ) : (
            <button onClick={handleSignIn}>Sign in with Google</button>
          )}
        </header>
        <div className="content">
          <Routes>
            <Route path="/create-task" element={<CreateTask />} />
            {/* Add more routes as needed */}
            <Route path="/" element={<Navigate to="/create-task" />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
