import React, { useState, useEffect } from 'react';
import { auth } from './services/firebase';
import { signInWithPopup, GoogleAuthProvider, onAuthStateChanged, signOut } from 'firebase/auth';
import './App.css'; // Import your global CSS styles
import SideMenu from './components/SideMenu';
import CreateTask from './components/CreateTask';
import Home from './components/Home';
import History from './components/History';
import Preferences from './components/Preferences'; // Import Preferences component

function App() {
  const [user, setUser] = useState(null);
  const [showPreferences, setShowPreferences] = useState(false);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, user => {
      setUser(user);
    });
    return () => unsubscribe();
  }, []);

  const handleSignIn = async () => {
    const provider = new GoogleAuthProvider();
    try {
      await signInWithPopup(auth, provider);
    } catch (error) {
      console.error('Google sign-in error:', error);
    }
  };

  const handleSignOut = async () => {
    try {
      await signOut(auth);
      // Optionally, navigate to another page after sign out
    } catch (error) {
      console.error('Sign-out error:', error);
    }
  };

  const handleShowPreferences = () => {
    setShowPreferences(true);
  };

  const handleHidePreferences = () => {
    setShowPreferences(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <div className="welcome">
            {user && window.location.pathname === '/' && (
              <p>Welcome, {user.displayName}!</p>
            )}
          </div>
          <div className="auth-buttons">
            {user ? (
              <button className="sign-out-btn" onClick={handleSignOut}>Sign out</button>
            ) : (
              <button className="sign-in-btn" onClick={handleSignIn}>Sign in with Google</button>
            )}
          </div>
        </div>
      </header>
      <SideMenu user={user} onShowPreferences={handleShowPreferences} />
      <div className="content">
        {window.location.pathname === '/' && <Home user={user} />}
        {window.location.pathname === '/create-task' && <CreateTask />}
        {window.location.pathname === '/history' && <History />}
        {showPreferences && <Preferences onClosePreferences={handleHidePreferences} />}
      </div>
    </div>
  );
}

export default App;
