import React, { useState } from 'react';
import { db } from '../services/firebase'; // Adjust path as necessary
import './CreateTask.css'; // Import your CSS for CreateTask component

const CreateTask = () => {
  const [taskName, setTaskName] = useState('');
  const [progress, setProgress] = useState('Not Started');
  const [description, setDescription] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [priority, setPriority] = useState('Low');
  const [showErrorMessage, setShowErrorMessage] = useState(false);

  const handleTaskNameChange = (event) => {
    setTaskName(event.target.value);
  };

  const handleProgressChange = (event) => {
    setProgress(event.target.value);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const handleDueDateChange = (event) => {
    setDueDate(event.target.value);
  };

  const handlePriorityChange = (event) => {
    setPriority(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    // Validate due date
    const currentDate = new Date();
    const selectedDate = new Date(dueDate);

    if (selectedDate < currentDate) {
      setShowErrorMessage(true);
      setTimeout(() => {
        setShowErrorMessage(false);
      }, 3000); // Hide error message after 3 seconds
      return;
    }

    try {
      const taskRef = await db.collection('tasks').add({
        taskName,
        progress,
        description,
        dueDate: new Date(dueDate), // Convert to Firestore Timestamp if necessary
        priority,
      });

      console.log('Task added with ID:', taskRef.id);

      // Clear form fields
      setTaskName('');
      setProgress('Not Started');
      setDescription('');
      setDueDate('');
      setPriority('Low');
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  return (
    <div className="create-task-container">
      <h2>Create New Task</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="taskName">Task Name:</label>
          <input
            type="text"
            id="taskName"
            value={taskName}
            onChange={handleTaskNameChange}
            required
            autoFocus
          />
        </div>
        <div className="form-group">
          <label htmlFor="progress">Progress on Completion:</label>
          <select
            id="progress"
            value={progress}
            onChange={handleProgressChange}
          >
            <option value="Not Started">Not Started</option>
            <option value="In Progress">In Progress</option>
            <option value="Needs Review">Needs Review</option>
            <option value="Done">Done</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            value={description}
            onChange={handleDescriptionChange}
            rows={5}
          />
        </div>
        <div className="form-group">
          <label htmlFor="dueDate">Due Date:</label>
          <input
            type="date"
            id="dueDate"
            value={dueDate}
            onChange={handleDueDateChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="priority">Priority:</label>
          <select
            id="priority"
            value={priority}
            onChange={handlePriorityChange}
          >
            <option value="Low">Low</option>
            <option value="Medium">Medium</option>
            <option value="High">High</option>
          </select>
        </div>
        <button type="submit">Create Task</button>
      </form>
      {showErrorMessage && (
        <div className="error-message">
          <p>Please select a future date for the due date.</p>
        </div>
      )}
    </div>
  );
};

export default CreateTask;
