// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { auth } from './services/firebase'; // Adjust path based on your project structure
import { onAuthStateChanged, GoogleAuthProvider, signInWithPopup, signOut } from 'firebase/auth'; // Import Firebase auth methods
import SideMenu from './components/SideMenu'; // Assuming SideMenu is a component in './components/SideMenu'
import CreateTask from './components/CreateTask'; // Assuming CreateTask is a component in './components/CreateTask'

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, user => {
      setUser(user);
    });
    return () => unsubscribe();
  }, []);

  const handleSignIn = async () => {
    const provider = new GoogleAuthProvider();
    try {
      await signInWithPopup(auth, provider);
    } catch (error) {
      console.error('Google sign-in error:', error);
    }
  };

  const handleSignOut = async () => {
    try {
      await signOut(auth);
    } catch (error) {
      console.error('Sign-out error:', error);
    }
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={
            <div>
              <header className="App-header">
                {user ? (
                  <>
                    <p>Welcome, {user.displayName}!</p>
                    <button onClick={handleSignOut}>Sign out</button>
                    <SideMenu /> {/* Ensure SideMenu component is imported and defined */}
                  </>
                ) : (
                  <button onClick={handleSignIn}>Sign in with Google</button>
                )}
              </header>
            </div>
          } />
          <Route path="/create-task" element={<CreateTask />} /> {/* Ensure CreateTask component is imported and defined */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
