import React, { useEffect, useState } from 'react';
import { db } from '../services/firebase';
import { collection, query, orderBy, onSnapshot, deleteDoc, doc, updateDoc } from 'firebase/firestore';
import './Home.css'; // Ensure this path matches your project structure
import { useNavigate } from 'react-router-dom'; // Import useNavigate from react-router-dom

const Home = ({ user }) => {
  const [tasks, setTasks] = useState([]);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [taskToDelete, setTaskToDelete] = useState(null);
  const [showConfirmationMessage, setShowConfirmationMessage] = useState(true); // State to manage showing confirmation message
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(true); // State to manage showing delete confirmation
  const navigate = useNavigate(); // Use useNavigate to get the navigate function

  useEffect(() => {
    const q = query(collection(db, 'tasks'), orderBy('createdAt'));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const fetchedTasks = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setTasks(fetchedTasks);
    });

    return () => unsubscribe();
  }, []);

  useEffect(() => {
    const storedPreference = localStorage.getItem('deleteConfirmation');
    if (storedPreference === 'false') {
      setShowConfirmationMessage(false);
    }
  }, []);

  const handleDelete = async (taskId) => {
    try {
      const taskRef = doc(db, 'tasks', taskId);
      await updateDoc(taskRef, { deleted: true, deletedAt: new Date() });
      setShowConfirmation(false); // Close confirmation modal
      navigate('/history'); // Navigate to the history page after deletion
    } catch (error) {
      console.error('Error marking task as deleted:', error);
    }
  };

  const toggleConfirmation = (taskId) => {
    setTaskToDelete(taskId);
    setShowConfirmation(true);
  };

  const closeConfirmation = () => {
    setShowConfirmation(false);
  };

  const handleToggleMessage = () => {
    const newPreference = !showDeleteConfirmation;
    setShowDeleteConfirmation(newPreference);
    localStorage.setItem('deleteConfirmation', String(newPreference));
  };

  const renderTasks = (status) => (
    tasks
     .filter(task => task.progress === status)
     .map(task => (
        <div key={task.id} className="task-card">
          <h3>{task.taskName}</h3>
          <p>{task.description}</p>
          <p>Due: {task.dueDate}</p>
          <p>Priority: {task.priority}</p>
          <button className="close-btn" onClick={() => toggleConfirmation(task.id)}>
            <span aria-hidden="true">&times;</span>
          </button>
        </div>
      ))
  );

  return (
    <div className="home-container">
      <h2>Welcome, {user ? user.displayName : 'Guest'}!</h2>
      <div className="tasks-board">
        <div className="column">
          <h3>Not Started</h3>
          {renderTasks('Not Started')}
        </div>
        <div className="column">
          <h3>In Progress</h3>
          {renderTasks('In Progress')}
        </div>
        <div className="column">
          <h3>Needs Review</h3>
          {renderTasks('Needs Review')}
        </div>
        <div className="column">
          <h3>Done</h3>
          {renderTasks('Done')}
        </div>
      </div>
      {showConfirmation && showConfirmationMessage && (
        <div className="overlay">
          <div className="modal">
            <p className="modal-message">Are you sure you want to remove this task?</p>
            <div className="modal-buttons">
              <button className="modal-btn" onClick={() => handleDelete(taskToDelete)}>Yes</button>
              <button className="modal-btn secondary" onClick={closeConfirmation}>No</button>
            </div>
            <label>
              <input type="checkbox" checked={!showDeleteConfirmation} onChange={handleToggleMessage} /> Don't show this message again
            </label>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
