import React, { useState } from 'react';
import './SideMenu.css';

const SideMenu = ({ user, onShowPreferences }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const handleLinkClick = (path) => {
    window.location.pathname = path; // Directly change URL for navigation
    setIsOpen(false); // Close the menu after navigation
  };

  const handlePreferencesClick = () => {
    onShowPreferences(); // Callback to show Preferences component
    setIsOpen(false); // Close the menu after showing Preferences
  };

  return (
    <div className={`side-menu ${isOpen ? 'open' : ''}`}>
      <button className="menu-toggle" onClick={toggleMenu}>
        {isOpen ? '✕' : '≡'}
      </button>
      {isOpen && (
        <nav>
          <ul>
            <li>
              <button className="menu-button" onClick={() => handleLinkClick('/')}>Home</button>
            </li>
            <li>
              <button className="menu-button" onClick={() => handleLinkClick('/create-task')}>Create Task</button>
            </li>
            <li>
              <button className="menu-button" onClick={() => handleLinkClick('/history')}>History</button>
            </li>
            <li>
              <button className="menu-button" onClick={handlePreferencesClick}>Preferences</button>
            </li>
          </ul>
          {user && (
            <div className="user-info">
              <img src={user.photoURL} alt="User Avatar" className="user-avatar" />
              <div className="user-details">
                <p className="user-name">{user.displayName}</p>
              </div>
            </div>
          )}
        </nav>
      )}
    </div>
  );
};

export default SideMenu;
