import React, { useState, useEffect } from 'react';
import { db } from '../services/firebase';
import { collection, query, orderBy, where, onSnapshot, deleteDoc } from 'firebase/firestore';
import { useHistory } from 'react-router-dom';
import './History.css';

const History = () => {
  const [deletedTasks, setDeletedTasks] = useState([]);
  const history = useHistory();

  useEffect(() => {
    const q = query(
      collection(db, 'tasks'),
      where('deleted', '==', true),
      orderBy('deletedAt', 'desc')
    );

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const fetchedTasks = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setDeletedTasks(fetchedTasks);
    });

    return () => unsubscribe();
  }, []);

  const handleDelete = async (taskId) => {
    try {
      await deleteDoc(doc(db, 'tasks', taskId));
      // Redirect to deleted page or perform any other action upon successful deletion
      history.push('/deleted');
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div className="history-container">
      <h2>Deleted Tasks History</h2>
      <div className="history-list">
        {deletedTasks.length > 0 ? (
          deletedTasks.map(task => (
            <div key={task.id} className="history-item">
              <p><strong>Task Name:</strong> {task.taskName}</p>
              <p><strong>Description:</strong> {task.description}</p>
              <p><strong>Deleted At:</strong> {task.deletedAt ? new Date(task.deletedAt.seconds * 1000).toLocaleString() : 'Unknown'}</p>
              <button onClick={() => handleDelete(task.id)}>Delete</button>
            </div>
          ))
        ) : (
          <p>No deleted tasks found.</p>
        )}
      </div>
    </div>
  );
};

export default History;
